import React, { useState } from 'react';

const Contact = () => {

  const [data, setData] = useState({
    fullname: '', phone: "", email: "", message: ""
  }
  );
  const formhandler = (event) => {
    event.preventDefault();
    const { name, value } = event.target;
    setData((prev) => {
      return { ...prev, [name]: value }
    })
  }

  const collectdata = (e)=>{
e.preventDefault();
return alert({fullname},{phone},{email}) 
  
  }

  return (
    <>
      <div className="my-5 mx-auto">
        <h1 className='text-center'> Welcome</h1>
      </div>
      <div className="container contact_div">
        <div className="row">
          <div className="col-md-6 col-10 mx-auto">
            <form onSubmit={collectdata}>

              <div className="mb-3">
                <label className="form-label">Name</label>
                <input type="text" name='fullname' onChange={formhandler} value={data.fullname} className="form-control" id="" placeholder="Enter Your Name" />
              </div>
              <div className="mb-3">
                <label className="form-label">Phone</label>
                <input type="number" name='contact' onChange={formhandler} value={data.phone} className="form-control" id="" placeholder="Enter Your Contact No" />
              </div>

              <div className="mb-3">
                <label className="form-label">Email address</label>
                <input type="email" name='email' onChange={formhandler} value={data.email} className="form-control" id="exampleFormControlInput1" placeholder="name@example.com" />
              </div>
              <div className="mb-3">
                <label for="exampleFormControlTextarea1" className="form-label">Example textarea</label>
                <textarea className="form-control" name='message' onChange={formhandler} value={data.message} id="exampleFormControlTextarea1" rows="3"></textarea>
              </div>

            </form>
          </div>
        </div>
      </div>
    </>
  )
}

export default Contact;
